// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract Veeko {
    struct Article {
        uint256 id;
        address author;
        string title;
        string content;
        uint256 upvotes;
        mapping(address => bool) hasUpvoted;
    }

    uint256 private articleCounter;
    mapping(uint256 => Article) public articles;

    event ArticleCreated(uint256 indexed id, address indexed author, string title);
    event ArticleUpvoted(uint256 indexed id, address indexed voter, uint256 newUpvoteCount);

    function createArticle(string memory _title, string memory _content) public {
        articleCounter++;
        Article storage newArticle = articles[articleCounter];
        newArticle.id = articleCounter;
        newArticle.author = msg.sender;
        newArticle.title = _title;
        newArticle.content = _content;
        newArticle.upvotes = 0;

        emit ArticleCreated(articleCounter, msg.sender, _title);
    }

    function upvoteArticle(uint256 _articleId) public {
        require(_articleId > 0 && _articleId <= articleCounter, "Invalid article ID");
        Article storage article = articles[_articleId];
        require(!article.hasUpvoted[msg.sender], "You have already upvoted this article");

        article.upvotes++;
        article.hasUpvoted[msg.sender] = true;

        emit ArticleUpvoted(_articleId, msg.sender, article.upvotes);
    }

    function getArticle(uint256 _articleId) public view returns (
        uint256 id,
        address author,
        string memory title,
        string memory content,
        uint256 upvotes
    ) {
        require(_articleId > 0 && _articleId <= articleCounter, "Invalid article ID");
        Article storage article = articles[_articleId];
        return (article.id, article.author, article.title, article.content, article.upvotes);
    }

    function getArticleCount() public view returns (uint256) {
        return articleCounter;
    }
}
